mapper.insert:
		insert into board(
		boardnum
		, id
		, type
		, title
		, content
		
			, originalfile
			, savedfile
		
		
			,secretpassword
			,secret
		

		)
		values(
		board_seq.nextval
		, #{id}
		, #{type}
		, #{title}
		, #{content}
		
			, #{originalfile}
			, #{savedfile}
		
		
			, #{secretpassword}
			, true
		
		)

mapper.delete:
delete from 
	board
where id = #{id} and boardnum = #{boardnum}

mapper.update:
update board
	set	
		title = #{title}
		, content = #{content}
		
			, originalfile
			= #{originalfile}
			,savedfile
			= #{savedfile}
		
where 
	boardnum = #{boardnum}
